sys_script_include.script:
var NeedItUtil = Class.create();
NeedItUtil.prototype = {
	initialize: function() {
    },

	isDatePast: function (strDate) {
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		if(testdate.before(rightnow)){
			return true;
		}
		else{
				return false;
			}
   },

	isDateToday: function (strDate) {
		var rightnow = new GlideDateTime();
		var today = rightnow.getLocalDate();

		var testdate = new GlideDateTime(strDate);
		var  whenNeededDate	= testdate.getLocalDate();

		if(today.compareTo(whenNeededDate) == 0){
				return true;
		}
		else{
				return false;
			}
   },

	isDateTodayAndBefore: function (strDate) {
		var rightnow = new GlideDateTime();
		var today = rightnow.getLocalDate();

		var testdate = new GlideDateTime(strDate);
		var  whenNeededDate	= testdate.getLocalDate();

		if(today.compareTo(whenNeededDate) >= 0){
				return true;
		}
		else{
				return false;
			}
   },

	type: 'NeedItUtil'
};
